insert into `product_subcategories` values('1','1','1','Inland & Nearshore','1','category_image_fpo.gif','59','54','inland-nearshore','<a href=\"javascript:void(0);\" id=\"top\"></a>\n					<div style=\"margin: 20px 0; overflow: auto;\">\n						<img src=\"/images/inland_nearshore_booms.jpg\" width=\"280\" height=\"194\" alt=\"\" style=\"display: block; float: right;\" />\n						<h4 style=\"color: #000; font-size: .95em;\">6\" Containment Booms</h4>\n						<ul style=\"list-style: disc inside; margin-top: 20px; font-size: .875em;\">\n							<li><a href=\"#6x6\" title=\"\">6\" x 6\" Containment Boom</a></li>\n							<li><a href=\"#6x9\" title=\"\">6\" x 9\" Containment Boom</a></li>\n							<li><a href=\"#6x12\" title=\"\">6\" x 12\" Containment Boom</a></li>\n						</ul>\n					</div>\n					<div>\n						<a href=\"javascript:void(0);\" id=\"6x6\"></a>\n						<table cellpadding=\"4\" cellspacing=\"0\" border=\"0\" style=\"margin-bottom: 10px;\">\n							<tr>\n								<td><img src=\"/images/6x6_containment_boom.jpg\" width=\"166\" height=\"75\" alt=\"\" style=\"display: block; float: left; margin: 0 10px 10px 0;\" /></td>\n								<td>\n									<h4 style=\"color: #000;\">6\" x 6\" Containment Boom</h4>\n									<ul>\n										<li>6\" Diameter Flotation</li>\n										<li>6\" Skirt Depth</li>\n										<li>1/4\" / 5/16\" / 3/8\" Galvanized Chain Ballast</li>\n										<li>22 oz. / sq. yd. Jaton or 24 oz. / sq. yd Jaton Plus fabric: orange or yellow</li>\n										<li>Aluminum quick latch or slide couplers are recommended</li>\n									</ul>\n								</td>\n							</tr>\n						</table>\n						<h4 style=\"margin-bottom: 20px; font-size: 14px;\">ACME Boom &amp; Baffle Fabrics: <a href=\"/fabrics\" title=\"ACME Boom &amp; Baffle Fabrics\">Click here</a></h4>\n						<p><strong>Recommended Section Lengths:</strong> 50\' and 100\'</p>\n						<p><em><strong>Recommended Use:</strong></em> 6 x 6 booms are used in any one or more of these conditions: moderate wind, shallow water or moderate current.</p>\n						<h4 style=\"font-size: 14px;\">Request A Quote: <a href=\"/request_quote\" title=\"Request A Quote\">Click here</a></h4>\n						<a href=\"#top\" title=\"\" style=\"color: #124c92; font-size: 12px; margin-top: 10px\">[Top]</a>\n					</div>\n					\n					<div style=\"margin-top: 40px;\">\n						<a href=\"javascript:void(0);\" id=\"6x9\"></a>\n						<table cellpadding=\"4\" cellspacing=\"0\" border=\"0\" style=\"margin-bottom: 10px;\">\n							<tr>\n								<td><img src=\"/images/6x9_containment_boom.jpg\" width=\"170\" height=\"75\" alt=\"\" style=\"display: block; float: left; margin: 0 10px 10px 0;\" /></td>\n								<td>\n									<h4 style=\"color: #000;\">6\" x 9\" Containment Boom</h4>\n									<ul>\n										<li>6\" Diameter Flotation</li>\n										<li>9\" Skirt Depth</li>\n										<li>1/4\" / 5/16\" / 3/8\" Galvanized Chain Ballast</li>\n										<li>22 oz. / sq. yd. Jaton or 24 oz. / sq. yd Jaton Plus fabric: orange or yellow</li>\n										<li>Aluminum quick latch or slide couplers are recommended</li>\n									</ul>\n								</td>\n							</tr>\n						</table>\n						<h4 style=\"margin-bottom: 20px; font-size: 14px;\">ACME Boom &amp; Baffle Fabrics: <a href=\"/fabrics\" title=\"ACME Boom &amp; Baffle Fabrics\">Click here</a></h4>\n						<p><strong>Recommended Section Lengths:</strong> 50\' and 100\'</p>\n						<p><em><strong>Recommended Use:</strong></em> 6\" x 9\" booms are favored by clean-up contractors and end-users because they cover a broader spectrum of applications and conditions.</p>\n						<h4 style=\"font-size: 14px;\">Request A Quote: <a href=\"/request_quote\" title=\"Request A Quote\">Click here</a></h4>\n						<a href=\"#top\" title=\"\" style=\"color: #124c92; font-size: 12px; margin-top: 10px\">[Top]</a>\n					</div>\n					\n					<div style=\"margin: 40px 0; overflow: auto;\">\n						<a href=\"javascript:void(0);\" id=\"6x12\"></a>\n						<table cellpadding=\"4\" cellspacing=\"0\" border=\"0\" styl