SET NAMES utf8;
SET FOREIGN_KEY_CHECKS = 0;

CREATE TABLE `accessories` (
  `id` int(11) NOT NULL auto_increment,
  `accessory` varchar(255) NOT NULL,
  `price` decimal(8,0) default NULL,
  `image` varchar(255) default NULL,
  `width` int(11) default NULL,
  `height` int(11) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;


CREATE TABLE `links` (
  `id` int(11) NOT NULL auto_increment,
  `position` int(11) default NULL,
  `url` varchar(255) default NULL,
  `title` varchar(255) default NULL,
  `desc` text,
  `logo_image` varchar(255) default NULL,
  `logo_width` int(11) default NULL,
  `logo_height` int(11) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=latin1;


CREATE TABLE `product_accessories` (
  `id` int(11) NOT NULL auto_increment,
  `product_id` int(11) NOT NULL,
  `accessory_id` int(11) NOT NULL,
  `position` int(11) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `product_categories` (
  `id` int(11) NOT NULL auto_increment,
  `position` int(11) NOT NULL,
  `category` varchar(255) NOT NULL,
  `active` tinyint(1) NOT NULL,
  `permalink` varchar(50) default NULL,
  `content` text,
  `image` varchar(255) default NULL,
  `image_width` int(11) default NULL,
  `image_height` int(11) default NULL,
  `image_alt` varchar(50) default NULL,
  `short_desc` varchar(255) default NULL,
  `show_subcategories` tinyint(1) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;


CREATE TABLE `product_sections` (
  `id` int(11) NOT NULL auto_increment,
  `product_subcategory_id` int(11) default NULL,
  `position` int(11) default NULL,
  `section` varchar(255) default NULL,
  `short_desc` varchar(255) default NULL,
  `list_image` varchar(255) default NULL,
  `width` int(11) default NULL,
  `height` int(11) default NULL,
  `content` text,
  `active` tinyint(1) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=latin1;


CREATE TABLE `product_subcategories` (
  `id` int(11) NOT NULL auto_increment,
  `product_category_id` int(11) NOT NULL,
  `position` int(11) NOT NULL,
  `subcategory` varchar(255) NOT NULL,
  `active` tinyint(1) NOT NULL,
  `image` varchar(255) default NULL,
  `width` int(11) default NULL,
  `height` int(11) default NULL,
  `permalink` varchar(50) default NULL,
  `content` text,
  `short_desc` varchar(255) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=latin1;


CREATE TABLE `products` (
  `id` int(11) NOT NULL auto_increment,
  `position` int(11) NOT NULL,
  `product` varchar(255) NOT NULL,
  `alternate_title` varchar(255) default NULL,
  `price` decimal(8,2) default NULL,
  `content` text,
  `large_image` varchar(100) default NULL,
  `large_image_width` int(11) default NULL,
  `large_image_height` int(11) default NULL,
  `list_image` varchar(255) default NULL,
  `list_image_width` int(11) default NULL,
  `list_image_height` int(11) default NULL,
  `short_desc` varchar(255) default NULL,
  `dimensions` varchar(50) default NULL,
  `qty_per_pkg` varchar(50) default NULL,
  `show_cart` tinyint(1) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=latin1;


CREATE TABLE `schema_info` (
  `version` int(11) default NULL
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `section_products` (
  `id` int(11) NOT NULL auto_increment,
  `product_section_id` int(11) NOT NULL,
  `product_id` int(11) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=latin1;


CREATE TABLE `subcategory_accessories` (
  `id` int(11) NOT NULL auto_increment,
  `product_subcategory_id` int(11) NOT NULL,
  `accessory_id` int(11) NOT NULL,
  `position` int(11) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `subcategory_products` (
  `id` int(11) NOT NULL auto_increment,
  `product_subcategory_id` int(11) NOT NULL,
  `product_id` int(11) NOT NULL,
  `position` int(11) default NULL,
  `permalink` varchar(50) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=latin1;


SET FOREIGN_KEY_CHECKS = 1;
